package java1;

public class P1_Q6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x = 12;
++x;
System.out.println("Final value of x : " +x);
x++;
System.out.println("Final value of x : " +x);
--x;
System.out.println("Final value of x : " +x);
x--;
System.out.println("Final value of x : " +x);
	}

}
